    public Cursor searchBicycle(String bicycleSeries, String bicycleKeyword) {

        db = helper.getReadableDatabase();

        String[] bicycleInformation = {"name", "brand", "year", "frame", "groupset", "price", "image1"};
        String[] search = {"", "", ""};
        int i = 0;

        StringTokenizer st = new StringTokenizer(bicycleKeyword);
        while(st.hasMoreTokens()){   //토근이 있는동안 while문이 실행됨
            search[i] = st.nextToken(); // 토근을 temp 변수에 저장
            System.out.println("temp="+search[i]);
            i++;
        }

        //select table
        String tableName = "bicycle_road";
        if(bicycleSeries.equals("road")) {
        } else if(bicycleSeries.equals("mtb")) {
            tableName = "bicycle_mtb";
        }

        //search the keyword
        Cursor c = db.query(tableName, null, null, null, null, null, null);
        if(i==1) {
            c = db.query(tableName, bicycleInformation, "name" + " LIKE '%" + bicycleKeyword + "%'", null, null, null, null);
        } else if(i==2) {
            c = db.query(tableName, bicycleInformation, "name" + " LIKE '%" + search[0] + "%'" +
                    " or " + "name" + " LIKE '%" + search[1] + "%'", null, null, null, null);
        } else if(i==3) {
            c = db.query(tableName, bicycleInformation, "name" + " LIKE '%" + search[0] + "%'" +
                    " or " + "name" + " LIKE '%" + search[1] + "%'" +
                    " or " + "name" + " LIKE '%" + search[2] + "%'", null, null, null, null);
        }
        return c;
    }
